  using nlohmann::json;
  json j = json::parse("{ \"hello\": \"world\", \"name\": \"Nick\" }");
  for (json::iterator it = j.begin(); it != j.end(); ++it) {
    std::cout << it.key() << " : " << it.value() << "\n";
  }
  for (auto pair : j) {
    std::cout << pair << "\n";
    // std::cout << pair.first << " : " << pair.second << "\n";
  }

  std::map<std::string, std::string> map { {"hello", "world"}, {"name", "Nick"} };
  for (auto& pair: map) {
    std::cout << pair.first << " : " << pair.second << "\n";
    //std::cout << pair << "\n";
