``` javascript
module.exports.updateRule = function *updateRule() {
    const mongo = this.mongo || undefined;
    assert(mongo, 'context is missing MongoDB');
    const rules = mongo.collection('RULE-rules');
    const triggers = this.request.body.triggers;
    const tokens = library.extract(triggers || []);
    this.checkParams('wid').notEmpty().len(24, 24);
    const tv = this.checkBody('triggers').notEmpty();
    tv.ensure(_.isArray(triggers), 'invalid triggers');
    tv.ensureNot(_.isEmpty(tokens), 'empty rule tokens');
    const clean = _.isEmpty(this.errors || false);
    if (!clean) { this.body = this.errors; return; }
    const wid = new ObjectID(this.params.wid || 0);
    this.log.info(`updating ${wid} RULE rule`);
    const rule = library.compile(wid, tokens);
    const [q, u] = [{ tag: wid }, { $set: rule }];
    yield rules.update(q, u, { upsert: true });
    this.body = { rule, triggers, wid };
};

module.exports.dryRun = function *dryRun() {
    const triggers = this.request.body.triggers;
    const tokens = library.extract(triggers || []);
    this.checkParams('wid').notEmpty().len(24, 24);
    const tv = this.checkBody('triggers').notEmpty();
    tv.ensure(_.isArray(triggers), 'invalid triggers');
    tv.ensureNot(_.isEmpty(tokens), 'empty rule tokens');
    const clean = _.isEmpty(this.errors || false);
    if (!clean) { this.body = this.errors; return; }
    const wid = new ObjectID(this.params.wid || 0);
    this.log.info(`dry-running ${wid} RULE rule`);
    const rule = library.compile(wid, tokens);
    this.body = { rule, triggers, wid };
};

module.exports.removeRule = function *removeRule() {
    const mongo = this.mongo || undefined;
    assert(mongo, 'context is missing MongoDB');
    const rules = mongo.collection('RULE-rules');
    this.checkParams('wid').notEmpty().len(24, 24);
    const clean = _.isEmpty(this.errors || false);
    if (!clean) { this.body = this.errors; return; }
    const wid = new ObjectID(this.params.wid || 0);
    this.log.info(`removing ${wid} RULE rule`);
    const q = { tag: wid.toString() }; // by ID
    const v = yield rules.findAndRemove(q);
    this.body = { rule: v.value, wid };
};

module.exports.queryRules = function *queryRules() {
    const mongo = this.mongo || undefined;
    assert(mongo, 'context is missing MongoDB');
    const rules = mongo.collection('RULE-rules');
    this.checkParams('wid').optional(); // opted
    this.checkQuery('skip').optional().toInt(0);
    this.checkQuery('limit').optional().toInt(0);
    const clean = _.isEmpty(this.errors || false);
    if (!clean) { this.body = this.errors; return; }
    const wid = new ObjectID(this.params.wid || 0);
    const q = {}; // baseline query; from scratch
    const o = this.query || {}; // query options
    if (this.params.wid) q.tag = wid; // set ID?
    const cursor = rules.find(q, o); // perform
    const results = yield cursor.toArray();
    const count = yield cursor.count();
    this.body = { results, count };
};
```

